import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';class ResetPasswordController extends GetxController {  bool loading = false;  reset_pass(context) async {    loading = true;    update();    await Future.delayed(Duration(seconds: 2)).then((value) async {      loading = false;      update();      ShowDialog(context);      await Future.delayed(Duration(milliseconds: 1500)).then((value) {        Navigator.of(context).pop();        push(context, pushAndRemoveAll(context, const MainHomeScreen()));      });    });  }  void ShowDialog(context) {    showDialog(      context: context,      builder: (context) {        return Dialog(          elevation: 10,          child: Container(            width: 343.w,            height: 252.h,            decoration: BoxDecoration(              color: Colors.white,              borderRadius: BorderRadius.circular(25),            ),            child: Column(              mainAxisAlignment: MainAxisAlignment.center,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                SizedBox(                  height: 66.h,                  width: 66.w,                  child: Image.asset(                      'assets/icons/${StaticData.checkTrueResetPass}'),                ),                space_h(28.h),                const Text(                  'لقد تم تغيير كلمة المرور بنجاح !',                  style: TextStyle(                    fontSize: 14,                    color: Colors.black,                    fontWeight: FontWeight.w700,                    fontFamily: 'NeoSans',                  ),                ),                space_h(34.h),                SizedBox(                  height: 48.h,                  width: 252.w,                  child: CustomButton(() {                    back(context);                  }, 'رجوع', AppColor.mainColorRed, Colors.white),                ),              ],            ),          ),        );      },    );  }  back(context) {    Navigator.pop(context);    Navigator.pop(context);  }}