import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:flutter/services.dart';import 'package:get/get.dart';class SignUpController extends GetxController {  bool maleSelected = true;  bool checkBoxSelected = false;  change_select() {    if (maleSelected) {      maleSelected = false;    } else {      maleSelected = true;    }    update();  }  changeCheckBoxSelected(bool value) {    checkBoxSelected = value;    update();  }  @override  void onInit() {    // TODO: implement onInit    SystemChrome.setSystemUIOverlayStyle(      SystemUiOverlayStyle(        statusBarColor: AppColor.mainColorRed,      ),    );    super.onInit();  }}