import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:intl/intl.dart';class ChairReservationController extends GetxController {  int numChairReservation = 1;  changeNumChair(bool add) {    if (add) {      numChairReservation++;    } else {      if (numChairReservation >= 2) {        numChairReservation--;      }    }    update();  }  String todayTime = '';  String dateTime = '';  String TimeWithDate = '';  FocusNode focusNode = FocusNode();  String formatTime(TimeOfDay time) {    final format = DateFormat('hh:mm a');    DateTime dateTime = DateTime(DateTime.now().year, DateTime.now().month,        DateTime.now().day, time.hour, time.minute);    return format.format(dateTime);  }  String formatDate(DateTime date) {    final format = DateFormat('dd/M/yyyy');    return format.format(date);  }  bool reservationToday = true;  changeReservationTime(bool newTime) {    reservationToday = newTime;    update();  }}