import 'package:coffe_play/data/static_data.dart';import 'package:flutter/cupertino.dart';import 'package:get/get.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';import '../../core/AppData/AppImage.dart';class DetailsCaffeController extends GetxController {  BitmapDescriptor markerIcon = BitmapDescriptor.defaultMarker;  void addCustomIcon() {    BitmapDescriptor.fromAssetImage(const ImageConfiguration(),            'assets/icons/${AppImageName.marker_icon}',mipmaps: true,)        .then((value) {      markerIcon = value;      update();    });  }  @override  void onInit() {    // TODO: implement onInit    addCustomIcon();    super.onInit();  }}