import 'package:awesome_dialog/awesome_dialog.dart';import 'package:coffe_play/core/AppData/AppImage.dart';import 'package:coffe_play/ui/screens/home/list_caffe_for_game_screen.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:meta/meta.dart';import '../../core/function/functions.dart';import '../../ui/screens/auth/choose_account_to_signIn.dart';class HomeController extends GetxController {  List<String> imgPath = [    'assets/images/home/${AppImageName.baloot}',    'assets/images/home/${AppImageName.chess}',  ];  List<String> gameName = [    'بلوت',    'شطرنج',  ];  GoTo_DetailsGame(context, title) {    push(context, ListCaffesAvailableScreen(title));  }  GoTO_SignIn(context) {    AwesomeDialog(      context: context,      animType: AnimType.scale,      dialogType: DialogType.warning,      btnOkColor: Colors.red,      title: 'قم بتسجيل الدخول أولاً',      desc: 'أنت مسجل الدخول كزائر قم بتسجيل الدخول لتتمكن من دخول اللعبة',      titleTextStyle: const TextStyle(        fontSize: 16,        fontFamily: 'NeoSans',        color: Colors.black,      ),      descTextStyle: const TextStyle(        fontSize: 12,        fontFamily: 'NeoSans',        color: Colors.black,      ),      btnOkOnPress: () {        push(context, const ChooseAccountToSignInScreen());      },      btnCancelOnPress: () {},      btnCancelColor: Colors.grey,    ).show();  }}