import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/ui/screens/auth/choose_account_to_signIn.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:flutter/cupertino.dart';import 'package:get/get.dart';class OnBoardingController extends GetxController {  PageController pageController = PageController(initialPage: 0);  int currentScreen = 0;  int lengthPages = 3;  next(context) {    currentScreen++;    if (currentScreen >= lengthPages) {      push_replcement(        context,        const MainHomeScreen(),      );    } else {      pageController.animateToPage(        currentScreen, duration: const Duration(milliseconds: 400),        curve: Curves.easeInToLinear, //easeInToLinear      );    }  }  skip(context) {    currentScreen = 3;    next(context);  }}