import 'package:coffe_play/ui/screens/auth/choose_account_to_signIn.dart';import 'package:flutter/cupertino.dart';import 'package:get/get.dart';import 'package:page_transition/page_transition.dart';class OnBoardingController extends GetxController {  PageController pageController = PageController(initialPage: 0);  int currentScreen = 0;  int lengthPages = 3;  next(context) {    currentScreen++;    if (currentScreen >= lengthPages) {      Navigator.pushReplacement(        context,        PageTransition(          type: PageTransitionType.leftToRight,          child: const ChooseAccountToSignInScreen(),          duration: const Duration(milliseconds: 700),          curve: Curves.easeInToLinear,        ),      );    } else {      pageController.animateToPage(        currentScreen,        duration: const Duration(milliseconds: 700),        curve: Curves.easeInToLinear,        //easeInToLinear      );    }  }  skip(context) {    currentScreen = 3;    next(context);  }}