import 'package:flutter/material.dart';import 'package:page_transition/page_transition.dart';push(context, Widget widget) {  return Navigator.push(    context,    PageTransition(      type: PageTransitionType.leftToRight,      child: widget,      duration: const Duration(milliseconds: 700),      curve: Curves.easeInToLinear,    ),  );}push_replcement(context, Widget widget) {  return Navigator.pushReplacement(    context,    PageTransition(      type: PageTransitionType.leftToRight,      child: widget,      duration: const Duration(milliseconds: 700),      curve: Curves.easeInToLinear,    ),  );}pushAndRemoveAll(context, Widget widget) {  return Navigator.pushAndRemoveUntil(    context,    PageTransition(      type: PageTransitionType.leftToRight,      child: widget,      duration: const Duration(milliseconds: 700),      curve: Curves.easeInToLinear,    ),    (route) => false,  );}