import 'package:coffe_play/localaization/app_locale.dart';import 'package:flutter/material.dart';import 'localaization_settings.dart';class TestTranslate extends StatelessWidget {  LocalizationSettings localizationSettings = LocalizationSettings();  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        actions: [          IconButton(            onPressed: () {              AppLocale.of(context).changeLanguage(Locale('ar'));            },            icon: const Icon(Icons.language),          ),        ],        title: Text(          getLang(context, "hello"),          style: const TextStyle(fontSize: 25),        ),      ),    );  }}