import 'package:coffe_play/network/api_srevices/api_url.dart';import 'package:coffe_play/network/check_internet.dart';import 'package:coffe_play/network/status_request.dart';import 'package:dio/dio.dart';import 'package:dartz/dartz.dart';class ApiServices {  static Dio dio = Dio();  static Future<Either<StatusRequest, dynamic>> getData({    required String endPoint,    required Map<String, dynamic> parameters,    required Map<String, dynamic> headers,  }) async {    String baseUrl = AppApiUrl.base_url;    if (await CheckInternet.checkInternetConnection()) {      try {        final response = await dio.get(          '$baseUrl$endPoint',          queryParameters: parameters,          options: Options(            headers: headers,          ),        );        if (response.statusCode == 200) {          return right(response.data);        } else {          return left(StatusRequest.serverFauilre);        }      } catch (e) {        return left(StatusRequest.failure);      }    } else {      return left(StatusRequest.offlineFailure);    }  }}