import 'dart:io';import 'package:awesome_dialog/awesome_dialog.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/AppData/AppImage.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/auth/signIn_screen.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';// import 'package:page_transition/page_transition.dart';class ChooseAccountToSignInScreen extends StatelessWidget {  const ChooseAccountToSignInScreen({super.key});  @override  Widget build(BuildContext context) {    SystemChrome.setSystemUIOverlayStyle(      SystemUiOverlayStyle(        statusBarColor: AppColor.mainColorRed,      ),    );    return WillPopScope(      onWillPop: () {        return AwesomeDialog(          context: context,          dialogType: DialogType.warning,          animType: AnimType.rightSlide,          title: 'هل أنت متأكد من الخروج من التطبيق',          titleTextStyle: TextStyle(            fontSize: 16,            fontWeight: FontWeight.w700,            fontFamily: 'NeoSans',            color: AppColor.mainColorRed,          ),          btnCancelOnPress: () {},          btnOkOnPress: () {            exit(0);          },        ).show().then((value) => value);      },      child: Scaffold(        body: CustomSignInBody(),      ),    );  }}class CustomSignInBody extends StatelessWidget {  const CustomSignInBody({super.key});  @override  Widget build(BuildContext context) {    return Column(      children: [        const CustomTopClipContainer(),        CustomButton(          () {            push(context, SignInScreen());          },          'تسجيل الدخول',          AppColor.mainColorRed,          Colors.white,        ),        space_h(15.h),        const CustomCreateAccountButton(),        space_h(24.h),        const CustomLogInAsVisitor(),      ],    );  }}class CustomLogInAsVisitor extends StatelessWidget {  const CustomLogInAsVisitor({super.key});  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: () {        pushAndRemoveAll(context, const MainHomeScreen());      },      child: const Text(        'دخول التطبيق كزائر',        style: TextStyle(            fontSize: 14,            fontWeight: FontWeight.w500,            fontFamily: 'NeoSans',            color: Color(0xFF454545)),      ),    );  }}class CustomCreateAccountButton extends StatelessWidget {  const CustomCreateAccountButton({super.key});  @override  Widget build(BuildContext context) {    return GestureDetector(        onTap: () {          push(context, SignUpScreen());        },        child: Container(          decoration: BoxDecoration(              borderRadius: BorderRadius.circular(8),              border: Border.all(                color: AppColor.mainColorRed,              )),          width: 343.w,          height: 48.h,          child: Center(            child: Text(              'إنشاء حساب جديد',              style: TextStyle(                fontSize: 18,                color: AppColor.mainColorRed,                fontWeight: FontWeight.w700,                fontFamily: 'NeoSans',              ),            ),          ),        ));  }}class CustomTopClipContainer extends StatelessWidget {  const CustomTopClipContainer({super.key});  @override  Widget build(BuildContext context) {    return ClipPath(      clipper: ClipperContainerOnBoarding(),      child: Container(        color: AppColor.mainColorRed,        height: 591.h,        child: Center(          child: SizedBox(            width: double.infinity,            height: double.infinity,            child: Stack(              alignment: Alignment.center,              children: [                SizedBox(                    height: 303.h,                    width: 302.w,                    child:                        Image.asset('assets/logo/${AppImageName.logo_game}')),                Positioned(                  bottom: 70.h,                  child: const Text(                    'تمتع بالجلسات الهادئة والألعاب الجميلة\n نتمنى لك أوقاتاً ممتعة',                    style: TextStyle(                      fontSize: 18,                      fontWeight: FontWeight.w500,                      fontFamily: 'NeoSans',                      color: Colors.white,                    ),                    textAlign: TextAlign.center,                  ),                ),              ],            ),          ),        ),      ),    );  }}class ClipperContainerOnBoarding extends CustomClipper<Path> {  @override  Path getClip(Size size) {    double width = size.width;    double height = size.height;    var path = Path();    path.lineTo(0, height - 50.h);    path.quadraticBezierTo(width / 2, height, width, height - 50.h);    path.lineTo(width, 0);    return path;  }  @override  bool shouldReclip(covariant CustomClipper<Path> oldClipper) {    return false;  }}