import 'package:coffe_play/controller/auth/forget_pass_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:coffe_play/ui/screens/auth/verify_code_screen.dart';import 'package:coffe_play/ui/screens/on_boarding/on_boarding_screen.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_title_and_sub_title.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:page_transition/page_transition.dart';class ForgetPasswordScreen extends StatelessWidget {  const ForgetPasswordScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(ForgetPasswordController());    return Directionality(        textDirection: TextDirection.rtl,        child: Scaffold(          body: SafeArea(            child: Padding(              padding: EdgeInsets.only(                top: 39.h,                right: 15.w,              ),              child: GetBuilder<ForgetPasswordController>(builder: (logic) {                return Column(                  // mainAxisSize: MainAxisSize.max,                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    space_w(double.infinity),                    Center(                        child: SizedBox(                            height: 234.h,                            child: Image.asset(                                'assets/signIn_signUp/${StaticData.forget_password_logo}'))),                    space_h(24.h),                    TopTextTitleAndSubTitle('إعادة تعيين كلمة المرور',                        'من فضلك, قم بإدخال بريدك الإلكتروني \nلإرسال رمز التحقق اليه'),                    CustomTitleAndTextField(                      'البريد الإلكتروني',                      'example@gmail.com',                      TextEditingController(),                      StaticData.email,                      TextInputType.emailAddress,                      false,                    ),                    space_h(34.h),                    CustomButton(                      () {                        Navigator.pushReplacement(                          context,                          PageTransition(                            type: PageTransitionType.leftToRight,                            child: const VerifyCodeScreen(),                            duration: const Duration(milliseconds: 700),                            curve: Curves.easeInToLinear,                          ),                        );                      },                      'إرســال',                      AppColor.mainColorRed,                      Colors.white,                    ),                  ],                );              }),            ),          ),        ));  }}