import 'package:coffe_play/controller/auth/reset_pass_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_title_and_sub_title.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:lottie/lottie.dart';class ResetPasswordScreen extends StatelessWidget {  const ResetPasswordScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(ResetPasswordController());    return Directionality(      textDirection: TextDirection.rtl,      child: Scaffold(        body: SafeArea(          child: Padding(            padding: EdgeInsets.only(top: 29.h, right: 15.w),            child: GetBuilder<ResetPasswordController>(builder: (logic) {              print(logic.loading);              return Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  Center(                    child: Image.asset(                        'assets/signIn_signUp/${StaticData.reset_pass_logo}'),                  ),                  space_h(20.h),                  TopTextTitleAndSubTitle('إعادة تعيين كلمة مرور جديدة',                      'يجب أن تكون كلمة مرورك الجديدة مختلفة\n عن كلمة المرور السابقة'),                  CustomTitleAndTextField(                    'كلمة المرور الجديدة',                    '***********',                    TextEditingController(),                    StaticData.lock,                    TextInputType.visiblePassword,                    true,                  ),                  CustomTitleAndTextField(                    'تأكيد كلمة المرور',                    '***********',                    TextEditingController(),                    StaticData.lock,                    TextInputType.visiblePassword,                    true,                  ),                  space_h(34.h),                  Stack(                    children: [                      CustomButton(                        () => logic.reset_pass(context),                        'إعادة تعيين كلمة المرور',                        AppColor.mainColorRed,                        Colors.white,                      ),                      Visibility(                          visible: logic.loading,                          child: Positioned(                        left: 50.w,                        top: 10.h,                        child: SizedBox(                          width: 30.w,                          height: 30.h,                          child: Lottie.asset(                            'assets/lottie/animation_loading.json',                          ),                        ),                      )),                    ],                  ),                ],              );            }),          ),        ),      ),    );  }}