import 'package:coffe_play/controller/auth/signIn_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/data/user_information.dart';import 'package:coffe_play/ui/screens/auth/forget_password_screen.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:coffe_play/ui/widgets/auth/sigIn_widgets/remember_me_and_foegetPass.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/ClipperTopContainerWithLogo.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_title_and_sub_title.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/footer_rich_text.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import '../../../core/AppData/AppImage.dart';import '../../../data/static_data.dart';class SignInScreen extends StatelessWidget {  const SignInScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(SignInContorller());    return Scaffold(      body: SingleChildScrollView(          physics: const BouncingScrollPhysics(),          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              CustomTopClipContainer_SignIn_SignUp_Screens(                  AppImageName.sigIn_logo),              Padding(                padding: EdgeInsets.symmetric(                  vertical: 15.h,                  horizontal: 12.w,                ),                child: GetBuilder<SignInContorller>(builder: (logic) {                  return Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      TopTextTitleAndSubTitle(                          'تسجيل دخول', 'أهلا وسهلا بك مرة أخرى'),                      CustomTitleAndTextField(                        'البريد الإلكتروني',                        'example@gmail.com',                        TextEditingController(),                        AppImageName.email,                        TextInputType.emailAddress,                        false,                      ),                      CustomTitleAndTextField(                        'كلمة المرور',                        '****************',                        TextEditingController(),                        AppImageName.lock,                        TextInputType.visiblePassword,                        true,                      ),                      space_h(34.h),                      CustomRememberMeBoxAndForgetPass(                          (p0) => logic.changeCheckBoxSelected(p0!),                          logic.checkBoxSelected),                      space_h(17.h),                      CustomButton(() {                        UserInformation.user = UserStatus.signedIn;                        pushAndRemoveAll(context, const MainHomeScreen());                      }, 'تسجيل الدخول', AppColor.mainColorRed, Colors.white),                      space_h(17.h),                      CustomRichText(true),                      space_h(20.h),                    ],                  );                }),              ),            ],          )),    );  }}