import 'package:coffe_play/controller/auth/signUp_contorller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/auth/signIn_screen.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/ClipperTopContainerWithLogo.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_select_sex.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_title_and_sub_title.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/list_title_textField.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:page_transition/page_transition.dart';import '../../widgets/auth/signUp_widgets/footer_rich_text.dart';import 'choose_account_to_signIn.dart';class SignUpScreen extends StatelessWidget {  const SignUpScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(SignUpController());    return Directionality(      textDirection: TextDirection.rtl,      child: Scaffold(        backgroundColor: AppColor.mainColorScaffoldWhite,        body: const CustomSignUpBody(),      ),    );  }}class CustomSignUpBody extends StatelessWidget {  const CustomSignUpBody({super.key});  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      physics: const BouncingScrollPhysics(),      child: GetBuilder<SignUpController>(builder: (logic) {        return Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            CustomTopClipContainer_SignIn_SignUp_Screens(StaticData.sigUp_logo),            Padding(              padding: EdgeInsets.symmetric(                vertical: 15.h,                horizontal: 12.w,              ),              child: Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  TopTextTitleAndSubTitle(                      'إنشاء حساب جديد', 'من فضلك, قم بتسجيل جميع البيانات !'),                  const CustomListTextField(),                  space_h(16.h),                  CustomSelectSexAndTitle(logic),                  space_h(12.h),                  CheckBoxAndTitle(                    (value) {                      logic.changeCheckBoxSelected(value!);                    },                    'أنا أوافق على جميع الشروط والأحكام',                    logic.checkBoxSelected,                  ),                  CustomButton(() {                    pushAndRemoveAll(context, const MainHomeScreen());                  }, 'إنشاء حساب جديد', AppColor.mainColorRed, Colors.white),                  space_h(12.h),                  CustomRichText(false),                  space_h(20.h),                ],              ),            ),          ],        );      }),    );  }}