import 'package:coffe_play/controller/auth/verify_code_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:coffe_play/ui/widgets/auth/signUp_widgets/custom_title_and_sub_title.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:lottie/lottie.dart';import 'package:pinput/pinput.dart';import '../../../core/AppData/AppImage.dart';class VerifyCodeScreen extends StatelessWidget {  const VerifyCodeScreen({super.key});  @override  Widget build(BuildContext context) {    Get.lazyPut(() => VerifyCodeController());    return Scaffold(      body: SafeArea(        child: GetBuilder<VerifyCodeController>(builder: (logic) {          return Column(            children: [              space_w(double.infinity),              Center(                child: SizedBox(                  height: 234.h,                  child: Image.asset(                      'assets/signIn_signUp/${AppImageName.verify_code}'),                ),              ),              space_h(24.h),              TopTextTitleAndSubTitle('التحقق من البريد الإلكتروني',                  'من فضلك, قم بإدخال رمز التفعيل الذي أرسلناه على \nالبريد الإلكتروني'),              space_h(28.h),              Directionality(                textDirection: TextDirection.ltr,                child: Pinput(                  controller: logic.pinController,                  length: 5,                  toolbarEnabled: false,                  defaultPinTheme: logic.defaultPinTheme,                  focusedPinTheme: logic.focusPinTheme,                  onCompleted: (code) {                    logic.verify(context);                  },                ),              ),              space_h(28.h),              // Visibility(              //   visible: !logic.isLoading,              //   child: CustomButton(() => null, 'تفعيل',              //       AppColor.mainColorRed, Colors.white),              // ),              Visibility(                  visible: logic.isLoading,                  child: Stack(                    children: [                      const CustomLoadingButton(),                      Positioned(                        left: 70.w,                        top: 10.h,                        child: SizedBox(                          width: 30.w,                          height: 30.h,                          child: Lottie.asset(                              'assets/lottie/animation_loading.json'),                        ),                      ),                    ],                  )),              space_h(28.h),              FooterText(),            ],          );        }),      ),    );  }}class CustomLoadingButton extends StatelessWidget {  const CustomLoadingButton({super.key});  @override  Widget build(BuildContext context) {    return SizedBox(        width: 327.w,        height: 50.h,        child: Stack(          children: [            CustomButton(                () => null, 'يتم التحقق ', AppColor.mainColorRed, Colors.white),          ],        ));  }}class FooterText extends StatelessWidget {  const FooterText({super.key});  @override  Widget build(BuildContext context) {    return Column(      children: [        const Text(          'لم يرسل لي رمز الإرسال ؟',          style: TextStyle(            color: Color(0xFFB4B8BD),            fontFamily: 'NeoSans',            fontSize: 12,            fontWeight: FontWeight.w600,          ),        ),        space_h(20.h),        Text(          'أعد الإرسال',          style: TextStyle(            color: AppColor.mainColorRed,            fontFamily: 'NeoSans',            fontSize: 13,            fontWeight: FontWeight.w600,          ),        ),      ],    );  }}