import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class CustomDateTimeContainer extends StatelessWidget {  Function() function;  String txt;  bool isSelected;  CustomDateTimeContainer(this.function, this.txt, this.isSelected);  @override  Widget build(BuildContext context) {    return GestureDetector(        onTap: function,        child: Container(          alignment: Alignment.centerRight,          padding: EdgeInsets.symmetric(            // vertical: 5.h,            horizontal: 12.w,          ),          width: double.infinity,          height: 48.h,          decoration: BoxDecoration(            borderRadius: BorderRadius.circular(10),            color: isSelected ?  Colors.white:Color(0xFFF6F6F6),          ),          child: CustomText(            txt,            14,            isSelected ?   Colors.black  : Colors.grey,            fontWeight: FontWeight.w500,          ),        ));  }}