import 'package:coffe_play/controller/details_caffe/chair_reservation_controller.dart';import 'package:coffe_play/ui/screens/caffe_details/reservation_payment/chair_reservation_screen.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'custom_date_and_time_container.dart';class ReservationInAotherDate extends StatelessWidget {  const ReservationInAotherDate({super.key});  @override  Widget build(BuildContext context) {    ChairReservationController logic = Get.find();    return Container(        decoration: BoxDecoration(          border: Border.all(              color: logic.reservationToday == false ? Colors.red : Colors.transparent          ),          color: logic.reservationToday == false              ? Color(0xFFF6F6F6)              : Colors.white,          borderRadius: BorderRadius.circular(15),        ),        padding: EdgeInsets.symmetric(          horizontal: 15.w,          vertical: 10.h,        ),        margin: EdgeInsets.symmetric(          vertical: 20.h,        ),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            space_h(10.h),            GestureDetector(              onTap: () {                logic.changeReservationTime(false);              },              child: Row(                children: [                  CustomText('حجز في وقت لاحق', 16, Color(0xFF454545)),                  Spacer(),                  Icon(                    logic.reservationToday == false                        ? Icons.circle                        : Icons.circle_outlined,                    size: 20,                    color: logic.reservationToday ? Colors.red : Colors.red,                  ),                ],              ),            ),            space_h(8.h),            CustomText('من فضلك حدد التاريخ والوقت المراد الحجز فيه', 12,                Color(0xFFB4B8BD)),            space_h(12.h),            space_h(12.h),            CustomDateTimeContainer(                  () {                if (logic.reservationToday == false) {                  showTimePicker(context: context, initialTime: TimeOfDay.now())                      .then((value) {                    if (value != null) {                      logic.TimeWithDate = logic.formatTime(value);                      logic.update();                    }                  });                }              },              logic.TimeWithDate.isEmpty                  ? logic.formatTime(TimeOfDay.now())                  : logic.TimeWithDate,logic.reservationToday == true ? false :true,            ),            space_h(20.h),            CustomDateTimeContainer(                  () {                if (logic.reservationToday == false){                  showDatePicker(                      context: context,                      initialDate: DateTime.now(),                      firstDate: DateTime.now(),                      lastDate: DateTime.now().add(Duration(days: 365)))                      .then((value) {                    if (value != null) {                      logic.dateTime = logic.formatDate(value);                      logic.update();                    }                  });                }              },              logic.dateTime.isEmpty                  ? logic.formatDate(DateTime.now())                  : logic.dateTime,logic.reservationToday == true ? false :true,            ),            space_h(10.h),          ],        ));  }}