// ignore_for_file: prefer_const_constructors, prefer_const_literals_to_create_immutablesimport 'package:coffe_play/controller/main_screens_controller/confrontation_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/AppData/AppImage.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/ui/screens/confrontation/details_confrontation_screen.dart';import 'package:coffe_play/ui/widgets/home/confrontation_widgets/top_title_and_choose_game.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';class ConfrontationScreen extends StatelessWidget {  const ConfrontationScreen({super.key});  @override  Widget build(BuildContext context) {    Get.lazyPut(() => ConfrontationController());    return GetBuilder<ConfrontationController>(builder: (logic) {      return Container(        color: AppColor.mainColorScaffoldWhite,        child: SafeArea(          child: Padding(            padding: EdgeInsets.symmetric(              horizontal: 10.w,              vertical: 10.h,            ),            child: Column(              children: [                TopTitleAndChooseGame(),                Row(                  children: [                    CustomText(                      'قائمة التحديات المتوفرة',                      14,                      AppColor.colorTextDarker,                    ),                    const Spacer(),                    CustomText(                      '(6)',                      12,                      Colors.red,                      fontWeight: FontWeight.bold,                    ),                  ],                ),                space_h(15.h),                Expanded(child: ListConfrontations()),              ],            ),          ),        ),      );    });  }}class ListConfrontations extends StatelessWidget {  const ListConfrontations({super.key});  @override  Widget build(BuildContext context) {    return ListView.builder(      physics: BouncingScrollPhysics(),      shrinkWrap: true,      itemCount: 10,      itemBuilder: (context, index) {        return CustomCardConfrontatioon();      },    );  }}class CustomCardConfrontatioon extends StatelessWidget {  const CustomCardConfrontatioon({super.key});  @override  Widget build(BuildContext context) {    ConfrontationController logic = Get.find();    return GestureDetector(      onTap: () {        push(context, DetailsConfrontation());      },      child: Card(        child: Padding(          padding: EdgeInsets.symmetric(            horizontal: 10.w,            vertical: 10.h,          ),          child: Row(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              SizedBox(                height: 86.h,                width: 90.h,                child: Image.asset(                  'assets/images/home/${AppImageName.cup}',                  fit: BoxFit.fill,                ),              ),              Column(                crossAxisAlignment: CrossAxisAlignment.start,                mainAxisSize: MainAxisSize.max,                mainAxisAlignment: MainAxisAlignment.start,                children: [                  CustomText(                    'تحدي ${logic.gameName[logic.select_confrontation]} الاسطوري',                    12,                    AppColor.colorTextDarker,                    fontWeight: FontWeight.w600,                  ),                  space_h(5.h),                  CustomRowDetailsCardConfrontation(                    Icons.price_change_outlined,                    "قيمة الاشتراك",                    '5 ريال شامل مشروب',                    false,                  ),                  CustomRowDetailsCardConfrontation(                    Icons.chair_outlined,                    "عدد المقاعد الكلي",                    '16 مقعد',                    false,                  ),                  CustomRowDetailsCardConfrontation(                    Icons.person_2_outlined,                    'عدد المنتسبين',                    '12 لاعب',                    true,                  ),                ],              ),              Spacer(),              SizedBox(                // height: 23.h,                width: 57.w,                child: Card(                  shape: RoundedRectangleBorder(                    borderRadius: BorderRadius.circular(15),                  ),                  color: Color(0xFF38E54D),                  child: Center(                    child: Padding(                      padding: EdgeInsets.symmetric(                        vertical: 5.h,                      ),                      child: CustomText(                        'متاح',                        10,                        Colors.white,                      ),                    ),                  ),                ),              ),            ],          ),        ),      ),    );  }}class CustomRowDetailsCardConfrontation extends StatelessWidget {  IconData icon;  String title;  String subTitle;  bool isNumPlayer;  CustomRowDetailsCardConfrontation(      this.icon, this.title, this.subTitle, this.isNumPlayer);  @override  Widget build(BuildContext context) {    return Padding(      padding: EdgeInsets.symmetric(vertical: 2.h),      child: Row(        children: [          Icon(            icon,          ),          space_w(2.w),          CustomText(            title,            10,            AppColor.colorText2,          ),          space_w(5.w),          CustomText(            subTitle,            10,            isNumPlayer ? Colors.red : AppColor.colorTextDarker,          ),        ],      ),    );  }}