import 'package:coffe_play/controller/main_screens_controller/home_controller.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';class HomeScreen extends StatelessWidget {  const HomeScreen({super.key});  @override  Widget build(BuildContext context) {    Get.lazyPut(()=>HomeController());    return GetBuilder<HomeController>(builder: (logic) {      return SafeArea(        child: SingleChildScrollView(            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                const TopStackMainHome(),                Padding(                  padding: EdgeInsets.symmetric(                    horizontal: 10.w,                  ),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      space_h(24.h),                      const Text(                        'قائمة الألعاب المتوفرة',                        style: TextStyle(                          color: Color(0xFF454545),                          fontFamily: 'NeoSans',                          fontSize: 16,                          fontWeight: FontWeight.w500,                        ),                      ),                      space_h(15.h),                      ...List.generate(                        logic.imgPath.length,                            (index) =>                            AvailableCardItemGames(                              logic.imgPath[index],                              logic.gameName[index],                              logic,                            ),                      ),                    ],                  ),                ),              ],            )),      );    });  }}