import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class ClipperContainerOnBoarding extends CustomClipper<Path> {  @override  Path getClip(Size size) {    double width = size.width;    double height = size.height;    var path = Path();    path.lineTo(0, height);    path.quadraticBezierTo(width / 2, height - 60.h, width, height);    path.lineTo(width, 0);    return path;  }  @override  bool shouldReclip(covariant CustomClipper<Path> oldClipper) {    return false;  }}