import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/data/static_data.dart';import 'package:coffe_play/ui/widgets/auth/onBoarding_widgets/ClipperContainer.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class CustomClipContainerWithLogo extends StatelessWidget {  int index;  CustomClipContainerWithLogo(this.index);  @override  Widget build(BuildContext context) {    return ClipPath(        clipper: ClipperContainerOnBoarding(),        child: Container(          color: AppColor.mainColorRed,          height: 507.h,          child: Center(            child: SizedBox(                width: 303.w,                height: 278.h,                child: Stack(                  alignment: Alignment.center,                  children: [                    Image.asset(                        'assets/on_boarding/${StaticData.background_logo_splash}'),                    Image.asset(                        'assets/on_boarding/${StaticData.on_boarding_logo[index]}'),                  ],                )),          ),        ));  }}