import 'package:coffe_play/controller/on_boarding/on_boarding_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';class CustomSkip extends StatelessWidget {  OnBoardingController logic;  CustomSkip(this.logic);  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: () {        logic.skip(context);      },      child: const Text(        'تجاوز',        style: TextStyle(            fontSize: 16,            fontWeight: FontWeight.w500,            fontFamily: 'NeoSans',            color: Color(0xFFB4B8BD)),      ),    );  }}class CustomNextButton extends StatelessWidget {  OnBoardingController logic;  CustomNextButton(this.logic);  @override  Widget build(BuildContext context) {    return CustomButton(          () => logic.next(context),      'التالي',      AppColor.mainColorRed,      Colors.white,    );  }}