import 'package:coffe_play/data/static_data.dart';import 'package:flutter/material.dart';class CustomTitle extends StatelessWidget {  int index;  CustomTitle(this.index);  @override  Widget build(BuildContext context) {    return Text(      StaticData.title[index],      style: const TextStyle(        fontSize: 18,        fontWeight: FontWeight.w500,        fontFamily: 'NeoSans',      ),    );  }}class CustomDescription extends StatelessWidget {  int index;  CustomDescription(this.index);  @override  Widget build(BuildContext context) {    return Text(      StaticData.description[index],      style: const TextStyle(        fontSize: 14,        fontWeight: FontWeight.w500,        color: Color(0xFFB4B8BD),        fontFamily: 'NeoSans',      ),      textAlign: TextAlign.center,    );  }}