import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:page_transition/page_transition.dart';import '../../../screens/auth/forget_password_screen.dart';class CustomRememberMeBoxAndForgetPass extends StatelessWidget {  Function(bool?) changeCheckBoxSelected;  bool checkBoxSelected;  CustomRememberMeBoxAndForgetPass(this.changeCheckBoxSelected, this.checkBoxSelected);  @override  Widget build(BuildContext context) {    return Row(      children: [        CheckBoxAndTitle(          changeCheckBoxSelected,          'تذكرني',          checkBoxSelected,        ),        Spacer(),        GestureDetector(          onTap: () {            Navigator.pushReplacement(              context,              PageTransition(                type: PageTransitionType.leftToRight,                child: const ForgetPasswordScreen(),                duration: const Duration(milliseconds: 700),                curve: Curves.easeInToLinear,              ),            );          },          child: const Text(            'لقد نسيت كلمة المرور ؟',            style: TextStyle(              color: Color(0xFFB4B8BD),              fontFamily: 'NeoSans',              fontSize: 11,              fontWeight: FontWeight.w600,            ),          ),        ),      ],    );  }}