import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class TopTextTitleAndSubTitle extends StatelessWidget {  String title;  String subTitle;  TopTextTitleAndSubTitle(this.title, this.subTitle);  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(          title,          style: TextStyle(            fontFamily: 'NeoSans',            color: Color(0xFF454545),            fontSize: 16,            fontWeight: FontWeight.w500,          ),        ),        space_h(12.h),        Text(          subTitle,          style: TextStyle(            fontFamily: 'NeoSans',            color: Color(0xFFB4B8BD),            fontSize: 14,            fontWeight: FontWeight.w500,          ),        ),      ],    );  }}