import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/core/function/functions.dart';import 'package:coffe_play/ui/screens/auth/signIn_screen.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:flutter/material.dart';class CustomRichText extends StatelessWidget {  bool isSignIn;  CustomRichText(this.isSignIn);  @override  Widget build(BuildContext context) {    return Align(      alignment: Alignment.center,      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Text(            isSignIn ? 'ليس لديك حساب ؟   ' : 'لدي حساب سابق ؟',            style: TextStyle(              color: Color(0xFF9FA6AA),              fontFamily: 'NeoSans',              fontSize: 11,              fontWeight: FontWeight.w600,            ),          ),          GestureDetector(            onTap: () {           push_replcement(context, isSignIn ? SignUpScreen() : SignInScreen(),);            },            child: Text(              isSignIn ? 'إنشاء حساب' : '   تسجيل الدخول',              style: TextStyle(                color: AppColor.mainColorRed,                fontFamily: 'NeoSans',                fontSize: 11,                fontWeight: FontWeight.w600,              ),            ),          ),        ],      ),    );  }}