import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/screens/auth/signIn_screen.dart';import 'package:coffe_play/ui/screens/auth/signUp_screen.dart';import 'package:flutter/material.dart';import 'package:page_transition/page_transition.dart';class CustomRichText extends StatelessWidget {  bool isSignIn;  CustomRichText(this.isSignIn);  @override  Widget build(BuildContext context) {    return Align(      alignment: Alignment.center,      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Text(            isSignIn ? 'ليس لديك حساب ؟   ' : 'لدي حساب سابق ؟',            style: TextStyle(              color: Color(0xFF9FA6AA),              fontFamily: 'NeoSans',              fontSize: 11,              fontWeight: FontWeight.w600,            ),          ),          GestureDetector(            onTap: () {              Navigator.pushReplacement(                context,                PageTransition(                  type: PageTransitionType.leftToRight,                  child: isSignIn ? SignUpScreen() : SignInScreen(),                  duration: const Duration(milliseconds: 700),                  curve: Curves.easeInToLinear,                ),              );            },            child: Text(              isSignIn ? 'إنشاء حساب' : '   تسجيل الدخول',              style: TextStyle(                color: AppColor.mainColorRed,                fontFamily: 'NeoSans',                fontSize: 11,                fontWeight: FontWeight.w600,              ),            ),          ),        ],      ),    );  }}