import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/widgets/home/chair_reservation_widgets/reservation_payment/payment_bottom_sheet.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';class CustomButtomGo extends StatelessWidget {  const CustomButtomGo({super.key});  @override  Widget build(BuildContext context) {    return  Align(      alignment: Alignment.center,      child: CustomButton(            () {          showModalBottomSheet(            shape: RoundedRectangleBorder(              borderRadius: BorderRadius.only(                topLeft: Radius.circular(20),                topRight: Radius.circular(20),              ),            ),            isScrollControlled: true,            context: context,            builder: (context) {              return CustomPaymentBottomSheet();            },          );        },        'إسـتـمـر',        AppColor.mainColorRed,        Colors.white,      ),    );  }}