// ignore_for_file: avoid_print import 'package:coffe_play/controller/main_screens_controller/details_confrontation_controller.dart';import 'package:coffe_play/controller/main_screens_controller/details_confrontation_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class CustomTabBar extends StatelessWidget {  const CustomTabBar({super.key});  @override  Widget build(BuildContext context) {    DetailsConfrontationController logic = Get.find();    return SizedBox(      height: 50,      child: AppBar(        elevation: 0,        backgroundColor: AppColor.mainColorScaffoldWhite,        bottom: TabBar(          // controller: tabController,          isScrollable: false,          physics: const NeverScrollableScrollPhysics(),          // dividerColor: Colors.red,          indicatorColor: Colors.red,          onTap: (value) {            print(value);            logic.change_select_confrontation(value);          },          tabs: [            Tab(              child: CustomText(                'تفاصيل التحدي',                14,                logic.select_confrontation == 0                    ? Colors.red                    : AppColor.colorText2,              ),            ),            Tab(              child: CustomText(                'تشكيل التحدي',                14,                logic.select_confrontation == 1                    ? Colors.red                    : AppColor.colorText2,              ),            ),          ],        ),      ),    );  }}