import 'package:coffe_play/controller/main_screens_controller/confrontation_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';class TopTitleAndChooseGame extends StatelessWidget {  const TopTitleAndChooseGame({super.key});  @override  Widget build(BuildContext context) {    ConfrontationController logic = Get.find();    return Column(      children: [        space_h(10.h),        Align(          alignment: Alignment.center,          child: CustomText(            'التحديات',            20,            AppColor.colorTextDarker,          ),        ),        space_h(10.h),        Container(          width: double.infinity,          height: 52.h,          decoration: BoxDecoration(            color: AppColor.mainColorScaffoldWhiteDarker,            borderRadius: BorderRadius.circular(15),          ),          child: ListView.builder(            physics: const BouncingScrollPhysics(),            scrollDirection: Axis.horizontal,            itemCount: 2,            itemBuilder: (context, index) {              return GestureDetector(                onTap: () {                  logic.change_connfrontation(index);                },                child: AnimatedContainer(                  // curve: Curves.fastLinearToSlowEaseIn,                    padding: EdgeInsets.symmetric(                      horizontal: 10.w,                    ),                    duration: const Duration(milliseconds: 500),                    margin: EdgeInsets.symmetric(                      vertical: 5.h,                      horizontal: 5.w,                    ),                    height: 42.h,                    width: 167.w,                    decoration: BoxDecoration(                      color: logic.select_confrontation != index                          ? AppColor.mainColorScaffoldWhiteDarker                          : Colors.red,                      borderRadius: BorderRadius.circular(10),                    ),                    child: Center(                      child: CustomText(                        'تحديات ${logic.gameName[index]}',                        14,                        logic.select_confrontation == index                            ? Colors.white                            : const Color(0xFFB4B8BD),                      ),                    )),              );            },          ),        ),        space_h(24.h),      ],    );  }}