import 'package:coffe_play/controller/details_caffe/details_caffe_controller.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:get/get.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';class CustomGoogleMap extends StatelessWidget {  const CustomGoogleMap({super.key});  @override  Widget build(BuildContext context) {    return GetBuilder<DetailsCaffeController>(builder: (logic) {      return Padding(        padding: EdgeInsets.symmetric(          vertical: 10.h,          horizontal: 10.w,        ),        child: SizedBox(          height: 200.h,          child: ClipRRect(            borderRadius: BorderRadius.circular(10),            child: GestureDetector(                behavior: HitTestBehavior.translucent,                child: GoogleMap(                  scrollGesturesEnabled: true,                  zoomGesturesEnabled: true,                  zoomControlsEnabled: true,                  buildingsEnabled: true,                  compassEnabled: true,                  markers: {                    Marker(                      icon: logic.markerIcon,                      markerId: const MarkerId('1'),                      position: const LatLng(24.724545, 46.630037),                    ),                  },                  // liteModeEnabled: true,                  indoorViewEnabled: true,                  initialCameraPosition: const CameraPosition(                    target: LatLng(24.724545, 46.630037),                    zoom: 16,                  ),                )),          ),        ),      );    });  }}