import 'package:coffe_play/ui/widgets/public_widgets.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'item_list_chairs_avaiable.dart';class CustomListHorizontalCaffeAvaiable extends StatelessWidget {  const CustomListHorizontalCaffeAvaiable({super.key});  @override  Widget build(BuildContext context) {    return Column(      children: [        Padding(          padding: const EdgeInsets.symmetric(vertical: 15),          child: Row(            children: [              CustomText('قائمة الطاولات المتوفرة', 14,                  const Color(0xFF454545)),              const Spacer(),              CustomText('( 4 )', 14, Colors.red),            ],          ),        ),        SizedBox(          height: 200.h,          child: ListView.separated(            physics: const BouncingScrollPhysics(),            shrinkWrap: true,            scrollDirection: Axis.horizontal,            itemBuilder: (context, index) {              return ItemListTableAvailable(index);            },            separatorBuilder: (context, index) {              return space_w(12.w);            },            itemCount: 4,          ),        ),        space_h(24.h),      ],    );  }}