import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/screens/home/MainHomeScreen.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/function/functions.dart';PreferredSizeWidget AppBarDetailsGame(String title, context) {  return AppBar(    centerTitle: true,    toolbarHeight: 60.h,    elevation: 0,    backgroundColor: AppColor.mainColorScaffoldWhite,    leading: IconButton(      onPressed: () {        Navigator.pop(context);        // pop(context, const MainHomeScreen());      },      icon: const Icon(        Icons.arrow_back_outlined,        size: 30,        color: Colors.black,      ),    ),    title: Text(      title,      style: const TextStyle(          fontSize: 16,          fontFamily: 'NeoSans',          color: Colors.black,          fontWeight: FontWeight.w600,          letterSpacing: 1.5),    ),  );}