import 'package:coffe_play/controller/home/list_caffe_available_controller.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:coffe_play/ui/screens/home/list_caffe_for_game_screen.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';class RowAvailableCaffe extends StatelessWidget {  const RowAvailableCaffe({super.key});  @override  Widget build(BuildContext context) {    return GetBuilder<ListCaffeAvaiableController>(builder: (logic) {      return Row(        children: [          const Text(            'قائمة الكافيهات القريبة',            style: TextStyle(              fontSize: 14,              fontFamily: 'NeoSans',              color: Colors.black,              fontWeight: FontWeight.w500,            ),          ),          const Spacer(),          Text(            '(${logic.searchController.text.isEmpty                ? logic.caffeName.length.toString()                : logic.searchListCaffe.length.toString()})',            style: TextStyle(              fontSize: 14,              fontFamily: 'NeoSans',              color: AppColor.mainColorRed,              fontWeight: FontWeight.w500,            ),          ),        ],      );    });  }}