import 'package:coffe_play/ui/widgets/home/list_caffe_for_game_widgets/custom_card_caffe.dart';import 'package:flutter/material.dart';import '../../../../controller/home/list_caffe_available_controller.dart';class CustomListCardCaffeAvailable extends StatelessWidget {  ListCaffeAvaiableController controller;  CustomListCardCaffeAvailable(this.controller);  @override  Widget build(BuildContext context) {    return ListView.builder(      // clipBehavior: Clip.antiAlias,      shrinkWrap: true,      physics: const BouncingScrollPhysics(),      itemCount: controller.searchController.text.isEmpty          ? controller.caffeName.length          : controller.searchListCaffe.length,      itemBuilder: (context, index) {        return CustomItemCardCaffe(          controller.searchController.text.isEmpty              ? controller.caffeName[index]              : controller.searchListCaffe[index],        );      },    );  }}