import 'dart:io';import 'package:awesome_dialog/awesome_dialog.dart';import 'package:coffe_play/core/AppData/AppColor.dart';import 'package:flutter/material.dart';Future<bool> willPopScopFunction(context) {  return AwesomeDialog(    context: context,    dialogType: DialogType.warning,    animType: AnimType.rightSlide,    title: 'هل أنت متأكد من الخروج من التطبيق',    titleTextStyle: TextStyle(      fontSize: 16,      fontWeight: FontWeight.w700,      fontFamily: 'NeoSans',      color: AppColor.mainColorRed,    ),    btnCancelOnPress: () {},    btnOkOnPress: () {      exit(0);    },  ).show().then((value) => value);}